import React, { useEffect, useState } from 'react';

function getProducts(basket_id) {
    return fetch(`http://localhost:3000/basket/products/${basket_id}`,
        {
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
            },
        })
        .then(response => response.json())
}
export default function Basket({ basket_id }) {
    const [products, setProducts] = useState([]);
    const [basket_total, setBasket_total] = useState(0);

    useEffect(() => {
        getProducts(basket_id)
            .then(products => {
                setProducts(products);
                setBasket_total(products.reduce((acc, curr) => acc + curr.price, 0));
            });

    }, []);

    const allProducts = () => {
        return (
            <div>
                {products.map(product => {
                    return (
                        <div key={product.id}>
                            <div>{product.name}</div>
                            <div>{product.price}</div>
                        </div>
                    )
                })}
            </div>
        )
    }
    return (
        <div className="basket">
            <h1>Basket</h1>
            <allProducts />
            <p>Toplam Tutar : {basket_total}</p>
        </div>
    );
}
