import React, { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent, CardActions, Button } from '@material-ui/core';

function getAddress(user_id) {
    return fetch(`http://localhost:3000/user/adress/${user_id}`, {
        method: 'GET',
        headers: {
            'Content-Type': 'application/json',
        },
    })
        .then(response => response.json())
}

export default function Address({ user_id }) {
    const [address, setAddress] = useState();
    useEffect(() => {
        fetchAdress()
    }, []);


    const fetchAdress = () => {
        getAddress(user_id).then(response => {
            setAddress(response);
        });
    };

    const DrawAddress = () => {
        console.log(address);
        if (address) {
            return (
                <Card>
                    <CardHeader title="Address" />
                    <CardContent>
                        <input
                            type="text"
                            placeholder="adress"
                            value={address.adress}
                            onChange={e => setAddress(address => ({ ...address, adress: e.target.value }))}
                        />

                        <p>{address.adress}</p>
                        <p>{address.city}</p>
                    </CardContent>
                    <CardActions>
                        <Button color="primary" onClick={fetchAdress}>
                            Update
                        </Button>
                    </CardActions>
                </Card>
            );
        }else { 
            return (
                <p>Loading ...</p>
            );
        }
    };



    return (

        <div>

            <DrawAddress />
        </div>

    );
}

