import React, { useEffect, useState } from 'react';
import { Card, CardHeader, CardContent, CardActions, Button } from '@material-ui/core';
import './Modals.css'

function fetchBrand(brand_id) {
    return fetch(`http://localhost:3000/brand/${brand_id}`, {
        method: 'GET',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
    })
        .then(response => response.json())
}

function VitaminCard({ vitamin }) {
    const [brand, setBrand] = useState();
    fetchBrand(vitamin.product.brand_id)
    .then(response => setBrand(response.name));

    return (
        <Card className="card-root">
            <CardHeader title={vitamin.product.name} subtitle={brand} />
            <CardContent>
                <h4></h4>
                <p>{vitamin.weight} mg</p>
                <p>â‚º{vitamin.product.price}</p>
            </CardContent>
            <CardActions>
                <Button color="primary" onClick={() => { console.log(vitamin.product.name); }}>Sepete Ekle</Button>
            </CardActions>
        </Card>
    );
}

export default VitaminCard;
